static gboolean
remote_mem_test_processor_dispatch_call (ThriftDispatchProcessor *dispatch_processor,
                                         ThriftProtocol *input_protocol,
                                         ThriftProtocol *output_protocol,
                                         gchar *method_name,
                                         gint32 sequence_id,
                                         GError **error)
{
  RemoteMemTestProcessorProcessFunction process_function; 
  gboolean dispatch_result = FALSE;

  RemoteMemTestProcessor *self = REMOTE_MEM_TEST_PROCESSOR (dispatch_processor);
  ThriftDispatchProcessorClass *parent_class =
    g_type_class_peek_parent (REMOTE_MEM_TEST_PROCESSOR_GET_CLASS (self));

  process_function = g_hash_table_lookup (self->process_map, method_name);
  if (process_function != NULL)
  {
    dispatch_result = (*process_function) (self,
                                           sequence_id,
                                           input_protocol,
                                           output_protocol,
                                           error);
  }
  else
  {
    dispatch_result = parent_class->dispatch_call (dispatch_processor,
                                                   input_protocol,
                                                   output_protocol,
                                                   method_name,
                                                   sequence_id,
                                                   error);
  }

  return dispatch_result;
}

static void
remote_mem_test_processor_set_property (GObject *object,
                                        guint property_id,
                                        const GValue *value,
                                        GParamSpec *pspec)
{
  RemoteMemTestProcessor *self = REMOTE_MEM_TEST_PROCESSOR (object);

  switch (property_id)
  {
    case PROP_REMOTE_MEM_TEST_PROCESSOR_HANDLER:
      if (self->handler != NULL)
        g_object_unref (self->handler);
      self->handler = g_value_get_object (value);
      g_object_ref (self->handler);
      break;
    default:
      G_OBJECT_WARN_INVALID_PROPERTY_ID (object, property_id, pspec);
      break;
  }
}

static void
remote_mem_test_processor_get_property (GObject *object,
                                        guint property_id,
                                        GValue *value,
                                        GParamSpec *pspec)
{
  RemoteMemTestProcessor *self = REMOTE_MEM_TEST_PROCESSOR (object);

  switch (property_id)
  {
    case PROP_REMOTE_MEM_TEST_PROCESSOR_HANDLER:
      g_value_set_object (value, self->handler);
      break;
    default:
      G_OBJECT_WARN_INVALID_PROPERTY_ID (object, property_id, pspec);
      break;
  }
}

static void
remote_mem_test_processor_dispose (GObject *gobject)
{
  RemoteMemTestProcessor *self = REMOTE_MEM_TEST_PROCESSOR (gobject);

  if (self->handler != NULL)
  {
    g_object_unref (self->handler);
    self->handler = NULL;
  }

  G_OBJECT_CLASS (remote_mem_test_processor_parent_class)->dispose (gobject);
}

static void
remote_mem_test_processor_finalize (GObject *gobject)
{
  RemoteMemTestProcessor *self = REMOTE_MEM_TEST_PROCESSOR (gobject);

  g_hash_table_destroy (self->process_map);

  G_OBJECT_CLASS (remote_mem_test_processor_parent_class)->finalize (gobject);
}

static void
remote_mem_test_processor_init (RemoteMemTestProcessor *self)
{
  self->handler = NULL;
  self->process_map = g_hash_table_new (g_str_hash, g_str_equal);

  g_hash_table_insert (self->process_map,
                       "ping", 
                       remote_mem_test_processor_process_ping);
  g_hash_table_insert (self->process_map,
                       "allocate_mem", 
                       remote_mem_test_processor_process_allocate_mem);
  g_hash_table_insert (self->process_map,
                       "read_mem", 
                       remote_mem_test_processor_process_read_mem);
  g_hash_table_insert (self->process_map,
                       "write_mem", 
                       remote_mem_test_processor_process_write_mem);
  g_hash_table_insert (self->process_map,
                       "free_mem", 
                       remote_mem_test_processor_process_free_mem);
}

static void
remote_mem_test_processor_class_init (RemoteMemTestProcessorClass *cls)
{
  GObjectClass *gobject_class = G_OBJECT_CLASS (cls);
  ThriftDispatchProcessorClass *dispatch_processor_class =
    THRIFT_DISPATCH_PROCESSOR_CLASS (cls);
  GParamSpec *param_spec;

  gobject_class->dispose = remote_mem_test_processor_dispose;
  gobject_class->finalize = remote_mem_test_processor_finalize;
  gobject_class->set_property = remote_mem_test_processor_set_property;
  gobject_class->get_property = remote_mem_test_processor_get_property;

  dispatch_processor_class->dispatch_call = remote_mem_test_processor_dispatch_call;
  cls->dispatch_call = remote_mem_test_processor_dispatch_call;

  param_spec = g_param_spec_object ("handler",
                                    "Service handler implementation",
                                    "The service handler implementation "
                                    "to which method calls are dispatched.",
                                    TYPE_REMOTE_MEM_TEST_HANDLER,
                                    G_PARAM_READWRITE);
  g_object_class_install_property (gobject_class,
                                   PROP_REMOTE_MEM_TEST_PROCESSOR_HANDLER,
                                   param_spec);
}
